const Stack = () => {
  const elementRef = useRef(null);

  gsap.registerPlugin(ScrollTrigger);

  const animateFrom = (direction = null) => {
    let anim;
    if (direction) {
      anim = gsap.utils.toArray(".scale__reveal").reverse();
    } else {
      anim = gsap.utils.toArray(".scale__reveal");
    }
    anim.forEach(function (elem: any, index) {
      gsap.fromTo(
        elem,
        { scale: 0, opacity: 0, autoAlpha: 0 },
        {
          duration: 1.25,
          scale: 1,
          opacity: 1,
          autoAlpha: 1,
          delay: index * 0.1,
          ease: "expo",
          overwrite: "auto",
        }
      );
    });
  };

  useEffect(() => {
    ScrollTrigger.create({
      trigger: elementRef.current,
      once: true,
      onEnter: function () {
        animateFrom();
      },
      onLeave: function () {},
    });
    return () => {};
  }, []);

  return (
    <div
      id="stack__container"
      ref={elementRef}
      className={style.stack__container}
    >
      {stacks.map((stack, i) => stack.svg)}
    </div>
  );
};

export default Stack;
